<script>
function appendAsyncScript(src){var scriptTag=document.createElement("script");scriptTag.setAttribute("src",src);scriptTag.setAttribute("async","");document.body.append(scriptTag);}
var OnePushSettings={appId:"a520fd64-1842-4263-a26b-b37ce38ee4ff",promptConditions:{timeOnPage:5000,pagesViewCount:2},notifyButton:{enable:false}};if(typeof isApp=='function'&&!isApp()){appendAsyncScript("https://cdn.onesignal.com/sdks/OneSignalSDK.js");var OneSignal=window.OneSignal||[];OneSignal.push(["init",{appId:OnePushSettings.appId,autoRegister:false,notifyButton:{enable:OnePushSettings.notifyButton.enable,prenotify:true,showCredit:false,text:{'tip.state.unsubscribed':'Đăng ký nhận thông báo','tip.state.subscribed':"Bạn đã đăng ký",'tip.state.blocked':"Bạn đã chặn đăng ký",'message.prenotify':'Click để đăng ký nhận thông báo','message.action.subscribed':"Cảm ơn bạn đã đăng ký",'message.action.resubscribed':"Bạn đã đăng ký thành công",'message.action.unsubscribed':"Bạn sẽ không nhận thông báo nữa",'dialog.main.title':'Quản lý thông báo','dialog.main.button.subscribe':'ĐĂNG KÝ','dialog.main.button.unsubscribe':'HUỶ ĐĂNG KÝ','dialog.blocked.title':'Huỷ chặn thông báo','dialog.blocked.message':"Thực hiện theo hướng dẫn sau để cho phép thông báo:"}},persistNotification:false,promptOptions:{actionMessage:"Hãy đăng ký thông báo để nhận những tin tức và khuyến mãi mới nhất từ Nguyễn Kim",acceptButtonText:"ĐỒNG Ý",cancelButtonText:"ĐỂ SAU"},welcomeNotification:{title:"Nguyễn Kim",message:"Cảm ơn bạn đã đăng ký!"}}]);setTimeout(function(){var page=location.pathname;var pagesView=JSON.parse(sessionStorage.getItem('pages_view'))||{};if(Object.keys(pagesView).length<OnePushSettings.promptConditions.pagesViewCount){pagesView[page]=true;sessionStorage.setItem('pages_view',JSON.stringify(pagesView));}
if(Object.keys(pagesView).length>=OnePushSettings.promptConditions.pagesViewCount){OneSignal.push(function(){OneSignal.showHttpPrompt();});}},OnePushSettings.promptConditions.timeOnPage);}</script>